mapper.insert:
update auth_func set updatetime = now()

	 ,name = #{name}
 

	 ,description = #{description}
  

	,url = #{url}
 

	,sort = #{sort}


	,tag = #{tag}
 
where funcid = #{funcId}

mapper.select:
select c.*,fp.privilege 
from auth_role r 
right join auth_rolefunc f on f.roleid = r.roleid 
right join auth_func c on c.funcid = f.funcid 
left join auth_rolefunc fp on f.funcid = fp.funcid and fp.roleid = #{roleId} 
where f.roleid = #{parentId};
